import React, { useRef } from 'react'
import {withRouter} from 'react-router-dom'

const SignUpForm = (props) => {
    const name = useRef();
    const n_name = useRef();
    const passwd = useRef();
    const birth_y = useRef();
    const region = useRef();

    const submit = async() => {
        const formData = {
            name : name.current.value,
            n_name : n_name.current.value,
            passwd : passwd.current.value,
            birth_y : birth_y.current.value,
            region : region.current.value,
        }
        await props.onSubmit(formData);

        props.history.push('/login');
    }
    const cancel = () => {
        props.history.goBack();
    }

    return(
        <div name="sentMessage" id="contactForm" novalidate>
            <div class="control-group">
                <div class="form-group floating-label-form-group controls">
                    <label>Name</label>
                    <input type="text" class="form-control" ref={name} placeholder="Name" id="name" required data-validation-required-message="Please enter your name." />
                </div>
            </div>
            <div class="control-group">
                <div class="form-group floating-label-form-group controls">
                    <label>Nick_Name</label>
                    <input type="text" class="form-control" ref={n_name} placeholder="Nick_Name" id="name" required data-validation-required-message="Please enter your nickname." />
                </div>
            </div>
            <div class="control-group">
                <div class="form-group floating-label-form-group controls">
                    <label>Password</label>
                    <input type="password" class="form-control" ref={passwd} placeholder="Password" required data-validation-required-message="Please enter your password." />
                    <p class="help-block text-danger"></p>
                </div>
            </div>
            <div class="control-group">
                <div class="form-group floating-label-form-group controls">
                    <label>Birth Year</label>
                    <input type="date" class="form-control" ref={birth_y} placeholder="Birth Year" required data-validation-required-message="Please enter your birth year." />
                    <p class="help-block text-danger"></p>
                </div>
            </div>
            <div class="control-group">
                <div class="form-group floating-label-form-group controls">
                    <label>Region</label>
                    <input type="text" class="form-control" ref={region} placeholder="Region" required data-validation-required-message="Please enter your region." />
                    <p class="help-block text-danger"></p>
                </div>
            </div>
            <br />
            <div id="success"></div>
            <button onClick={() => submit()} class="btn btn-primary" id="sendMessageButton">Send</button>
          &nbsp;&nbsp;&nbsp;&nbsp;
            <button class="mg-bottom-3" onClick={() => cancel()} class="btn btn-primary" id="sendMessageButton">Cancel</button>
        </div>
    )
}

export default withRouter(SignUpForm);
