import React, { useState, useEffect } from 'react'
import request from '../libs/request'
import { useLocation, withRouter } from 'react-router-dom'
import ArticleS from '../components/ArticleS'
import CommentList from '../components/CommentList'
import CommentForm from '../components/CommentForm'

const ArticleSpecific = (props) => {
    const location = useLocation();
    const [article, setArticle] = useState(location.state);
    const [comments, setComments] = useState();

    const formData = {
        article : article,
        user : window.sessionStorage.getItem('n_name')
    }

    useEffect(() => {
        async function fetchData() {
            setArticle(location.state);
            let temp = await request.getComments(location.state._id);
            setComments(temp);
        }
        fetchData();
    }, [])

    const setHead = (() => {
        props.headerSet(article);
    })();

    const eventHandle = async () => {
        let temp = await request.getComments(location.state._id);
        setComments(temp);
    }

    return (
        <div class="container">
            <div class="row">
                <div class="col-lg-8 col-md-10 mx-auto">
                    <button class="float-r" onClick={()=>request.updateArticle(formData)}>수정</button>
                    <button class="float-r" onClick={()=>request.deleteArticle(formData)}>삭제</button>
                    <ArticleS article={article} />
                    <div class="card card-outline-secondary my-4">
                        <div class="card-header">
                            Comments
                        </div>
                        <div class="card-body">
                            <CommentList comments={comments}/>
                        </div>
                    </div>

                    <CommentForm article={article} onSubmit={request.postComment} onHandle={()=>eventHandle()}/>

                </div>
            </div>
        </div>
    )
}

export default withRouter(ArticleSpecific);
