import React from 'react'
import Star from './Star'
import moment from 'moment'

const Article = (props) => {
    const { article } = props;
    const content = article.content.length>40?article.content.substr(0,40) + '...':article.content;
    return(
        <div class="post-preview">
          <a>
            <h2 class="post-title">
              {article.title}
            </h2>
            <h5 class="post-content">
              {content}
            </h5>
          </a>
          <p class="post-meta">Posted by <a>{article.author.n_name}</a> on {moment(article.date).format('YYYY-MM-DD, hh:mm')}</p>
            <span class="text-warning">
                <Star rating={article.rating} />
            </span>
            <hr />
        </div>
    )
}

export default Article;
