import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'

const SideNav = ({onHandle}) => {
    const [opt, setOpt] = useState(0);

    useEffect(()=>{
        onHandle(opt);
    }, [])

    return (
        <div class="col-lg-3">
            <h1 class="my-4">Articles</h1>
            <div class="list-group">
                <Link to='/' onClick={setOpt(0)} class={opt===0?"list-group-item active":"list-group-item"}>Recent</Link>
                <Link to='/' onClick={setOpt(1)} class={opt===1?"list-group-item active":"list-group-item"}>Major</Link>
                <Link to='/' onClick={setOpt(2)} class={opt===2?"list-group-item active":"list-group-item"}>Dream</Link>
            </div>
        </div>
    )
}
export default SideNav;
