import React, { useRef } from 'react'
import {withRouter} from 'react-router-dom'

const ArticleForm = (props) => {
    let form = {
        title : React.useRef(),
        content : React.useRef(),
        author : React.useRef()
    }
    const submit = async() => {
        const formData = {
            title : form.title.current,
            content : form.content.current,
            author : form.author.current
        }
        await props.onSubmit(formData);

        props.history.push('/');
    }
    const cancel = () => {
        props.history.goBack();
    }
    return(
        <>
            <div>Title : <input ref={form.title}></input></div>
            <div>Content : <input ref={form.content}></input></div>
            <div>Author : <input ref={form.author}></input></div>
            <button onClick={()=>submit()}>작성</button>
        </>
    )
}

export default withRouter(ArticleForm);
