import React, { useState, useEffect } from 'react'
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css'
import Nav from '../components/Nav'
import Header from '../components/Header'
import Footer from '../components/Footer'
import Articles from './Articles'
import LogIn from './LogIn'
import SignUp from './SignUp'
import CreateArticle from './CreateArticle'
import ArticleSpecific from './ArticleSpecific'
import request from '../libs/request'


const App = () => {
  const [isLogged, setIsLogged] = useState(false);
  const [article, setArticle] = useState();

  const onLogIn = () => {
    setIsLogged(true);
  }
  const onLogOut = async () => {

    await request.logOut();

    setIsLogged(false);
    window.sessionStorage.clear();
  }
  const setHeader = (article) => {
    setArticle(article);
  }
  useEffect(() => {
    const n_name = window.sessionStorage.getItem('n_name');
    if (n_name) onLogIn();
    else onLogOut();
  });

  return (
    <Router>
      <Nav isLogged={isLogged} onLogOut={onLogOut} onLogIn={onLogIn} />
      <Header article={article}/>
      <Switch>
        <Route exact path='/' component={Articles} />
        <Route exact path='/article/post' component={CreateArticle} />
        <Route exact path='/login' render={() => <LogIn onLogIn={onLogIn} />} />
        <Route exact path='/signup' component={SignUp} />
        <Route exact path='/article/:id' render={()=> <ArticleSpecific setHeader={setHeader} />} />
      </Switch>
      <Footer />
    </Router>
  )
}



export default App;
