import React, { useState, useEffect } from 'react'
import moment from 'moment'
import { useLocation } from 'react-router-dom'

const Header = (props) => {
    const { article } = props;
    //const [article, setArticle] = useState(props.article);
    //const {author, date, title} = props.article;
    const location = useLocation().pathname;
    /*
    useEffect(()=>{
        setArticle(props.article);
    },[])
    */
    return (
        <header class="masthead" id="home">
            <div class="overlay"></div>
            <div class="container">
                <div class="row">
                    <div class="col-lg-8 col-md-10 mx-auto">
                        <div class="site-heading">
                            {location === `/article/${article._id}` &&
                                <>
                                {console.log(article)}
                                    <h1>{article.title}</h1>
                                    <span class="meta">Posted by {article.author.n_name} on {moment(article.date).format('YYYY-MM-DD, hh:mm')}</span>
                                </>
                            }
                            {location === '/login' &&
                                <>
                                    <h1>Login</h1>
                                    <span class="subheading">로그인하셈ㅋ</span>
                                </>
                            }
                            {location === '/' &&
                                <>
                                    <h1>Articles</h1>
                                    <span class="subheading">게시물들임ㅋ</span>
                                </>
                            }
                            {location === '/signup' &&
                                <>
                                    <h1>Sign Up</h1>
                                    <span class="subheading">가입하셈ㅋ</span>
                                </>
                            }
                        </div>
                    </div>
                </div>
            </div>
        </header>
    )
}

export default Header;
