import React, { useState } from 'react'
import { withRouter } from 'react-router-dom'
import Article from './Article'
import _ from 'lodash'

const List = (props) => {
    const [articles, setArticles] = useState({
        data: props.list,
        pageSize: 5,
        currentPage: 1
    });
    const posting = () => {
        if (window.sessionStorage.getItem('n_name')) props.history.push('/article/post');
        else {
            window.alert('로그인이 필요합니다!');
            props.history.push('/login');
        }
    }
    function paginate(items, pageNumber, pageSize) {
        const startIndex = (pageNumber - 1) * pageSize; // 자를 배열의 시작점

        return _(items)
            .slice(startIndex) // 시작점부터 배열을 자르되
            .take(pageSize) // pageSize만큼의 배열을 취함
            .value(); // lodash wrapper 객체를 regular 배열로 변환
    }
    const handlePageChange = (page) => {
        setArticles({ ...articles, currentPage: page });
    };
    const { data, pageSize, currentPage } = articles;
    const pagedArticles = paginate(data, pageSize, currentPage);
    return (
        <>
            {pagedArticles.map(article =>
                <Article article={article} />
            )}
            <div div class="clearfix" >
                <button class="btn btn-primary float-right" onClick={() => posting()}>Post &rarr;</button>
            </div >
        </>
    )
}

export default withRouter(List);
