import axios from 'axios'

const API_DEFAULT = "http://localhost:3030/";
const instance = axios.create({ baseURL : API_DEFAULT });

export async function getArticles() {
    const result = await instance.get('/');
    return result.data;
}
export async function createArticle( {title, content} ) {
    const result = await instance.post('/article/create', {title, content});
    return result.data;
}
export async function signUp( {name, n_name, passwd, birth_y, region} ) {
    const result = await instance.post('/user/signup', {name, n_name, passwd, birth_y, region});
    return result.data;
}
export async function logIn( {n_name, passwd }){
    const result = await instance.post('/user/login', {n_name, passwd});
    return result.data;
}

export default {
    getArticles,
    createArticle,
    signUp,
    logIn
}
