import React from 'react'
import {Link} from 'react-router-dom'

const ArticleS = (props) => {
  return (
    <div class="card mt-4">

      <div class="card-body">
        {props.article?.author?.n_name === window.sessionStorage.getItem('n_name') &&
          <>
            <Link class="float-r btn btn-sm" to='/article/edit'>수정</Link>
            <button class="float-r btn btn-sm" onClick={() => props.onDelete(props.article._id)}>삭제</button>
          </>
        }
        <h4 class="card-title">{props.article.title}</h4>
        <hr />
        <p class="card-text content">{props.article.content}</p>
      </div>
    </div>
  )
}

export default ArticleS;
