import React, { useState, useEffect } from 'react'
import request from '../libs/request'
import {Link,  useLocation} from 'react-router-dom'
import ArticleS from '../components/ArticleS'
import CommentList from '../components/CommentList'
import CommentForm from '../components/CommentForm'

const ArticleSpecific = (props) => {
    const location = useLocation();
    const [article] = useState(location.state);
    const [comments, setComments] = useState();
    
    useEffect(()=> {
        async function fetchData(){
            let temp = await request.getComments(location.state._id);
            setComments(temp);
        }
        fetchData();
    })

    const setHead = (() => {
        props.headerSet(article);
    })();

    return(
        <div class="container">
            <div class="row">
                {console.log(article)}
                <div class="col-lg-3">
                    <h1 class="my-4">Caterogies</h1>
                    <div class="list-group">
                        <Link to='/' class="list-group-item active">Category 1</Link>
                        <Link to='/' class="list-group-item">Category 2</Link>
                        <Link to='/' class="list-group-item">Category 3</Link>
                    </div>
                </div>
                <div class="col-lg-8 col-md-10 mx-auto">

                    <ArticleS article={article}/>
                    <div class="card card-outline-secondary my-4">
                        <div class="card-header">
                            Comments
                        </div>
                        <CommentList comments={comments}/>
                    </div>

                    <CommentForm article={article} onSubmit={request.postComments}/>

                </div>
            </div>
        </div>
    )
}

export default ArticleSpecific;
