import React, { useState, useEffect } from 'react'
import { Link, useLocation } from 'react-router-dom'

const SideNav = ({onHandle}) => {
    const location = useLocation().pathname;
    const [opt, setOpt] = useState(0);

    useEffect(()=>{
        if(location==='/')
        {
            setOpt(0);
            onHandle(opt);
        }
        else if(location.indexOf('major') !== -1)
        {
            setOpt(1)
            onHandle(opt)
        }
        else if(location.indexOf('dream') !== -1)
        {
            setOpt(2)
            onHandle(opt)
        }
        else
            setOpt(3)
    })

    return (
        <div class="col-lg-3">
            <h1 class="my-4">Articles</h1>
            <div class="list-group">
                <Link to='/' class={opt==0?"list-group-item active":"list-group-item"}>Recent</Link>
                <Link to='/' class={opt==1?"list-group-item active":"list-group-item"}>Major</Link>
                <Link to='/' class={opt==2?"list-group-item active":"list-group-item"}>Dream</Link>
            </div>
        </div>
    )
}
export default SideNav;
