import React, { useState, useRef, useEffect } from 'react'
import {withRouter} from 'react-router-dom'
import request from '../libs/request'

const ArticleForm = (props) => {
    const title = useRef();
    const content = useRef();
    const [author, setAuthor] = useState();

    useEffect(()=>{
        async function fetchData(){
            const user = await request.getUser(window.sessionStorage.getItem('n_name'));
            setAuthor(user);
        }
        fetchData();
    },[])
    
    const submit = async() => {
        const formData = {
            title : title.current.value,
            content : content.current.value,
            author : author
        }
        console.log(formData);
        const article = await props.onSubmit(formData);
        await request.setUserArticle(article);

        props.history.push('/');
    }
    const cancel = () => {
        props.history.goBack();
    }
    return(
        <>
            <div>Title : <input ref={title}></input></div>
            <div>Content : <input ref={content}></input></div>
            <button onClick={()=>submit()}>작성</button>
            <button onClick={()=>cancel()}>취소</button>
        </>
    )
}

export default withRouter(ArticleForm);
