import React, { useRef } from 'react'
import { withRouter } from 'react-router-dom'

const SignUpForm = (props) => {
    const name = useRef();
    const n_name = useRef();
    const passwd = useRef();
    const birth = useRef();
    const region = useRef();

    const submit = async () => {
        const birth_y = birth.current.value.split("-")?.[0];
        const birth_m = birth.current.value.split("-")?.[1];

        const formData = {
            name: name.current.value,
            n_name: n_name.current.value,
            passwd: passwd.current.value,
            birth_y: birth_y,
            birth_m: birth_m,
            region: region.current.value,
        }
        const result = await props.onSubmit(formData);

        if (result.success === true) props.history.push('/login');
        else window.alert('닉네임이 중복되었습니다!');
    }
    const cancel = () => {
        props.history.goBack();
    }

    return (
        <div name="sentMessage" id="contactForm" novalidate>
            <div class="control-group">
                <div class="form-group floating-label-form-group controls">
                    <label>Name</label>
                    <input type="text" class="form-control" ref={name} placeholder="Name" />
                </div>
            </div>
            <div class="control-group">
                <div class="form-group floating-label-form-group controls">
                    <label>Nick_Name</label>
                    <input type="text" class="form-control" ref={n_name} placeholder="Nick_Name" />
                </div>
            </div>
            <div class="control-group">
                <div class="form-group floating-label-form-group controls">
                    <label>Password</label>
                    <input type="password" class="form-control" ref={passwd} placeholder="Password" />
                    <p class="help-block text-danger"></p>
                </div>
            </div>
            <div class="control-group">
                <div class="form-group floating-label-form-group controls">
                    <label>Birth</label>
                    <input type="month" class="form-control" ref={birth} placeholder="Birth" />
                    <p class="help-block text-danger"></p>
                </div>
            </div>
            <div class="control-group">
                <div class="form-group floating-label-form-group controls">
                    <label>Region</label>
                    <input type="text" class="form-control" ref={region} placeholder="Region" />
                    <p class="help-block text-danger"></p>
                </div>
            </div>
            <br />
            <div id="success"></div>
            <button onClick={() => submit()} class="btn btn-primary">Send</button>
          &nbsp;&nbsp;&nbsp;&nbsp;
            <button onClick={() => cancel()} class="btn btn-primary mg-bottom-3">Cancel</button>
        </div>
    )
}

export default withRouter(SignUpForm);
