import React, { useState, useEffect } from 'react'
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css';
import 'https://fonts.googleapis.com/css?family=Lora:400,700,400italic,700italic'
import 'https://fonts.googleapis.com/css?family=Open+Sans:300italic,400italic,600italic,700italic,800italic,400,300,600,700,800'
import 'vendor/fontawesome-free/css/all.min.css'
import '../App.css'
import Nav from '../components/Nav'
import Header from '../components/Header'
import Articles from './Articles'
import LogIn from './LogIn'
import SignUp from './SignUp'
import CreateArticle from './CreateArticle'
import request from '../libs/request'


const App = () => {
  const [isLogged, setIsLogged] = useState(false);

  const onLogIn = () => {
    setIsLogged(true);
  }
  const onLogOut = async () => {
    console.log(isLogged);

    await request.logOut();

    setIsLogged(false);
    window.sessionStorage.clear();
  }
  useEffect(()=>{
    const n_name = window.sessionStorage.getItem('n_name');
    if(n_name) onLogIn();
    else onLogOut();
  });

  return(
    <Router>
      <Nav isLogged={isLogged} onLogOut={onLogOut} onLogIn={onLogIn}/>
      <Header />
      <Switch>
        <Route exact path='/' component={Articles} />
        <Route exact path='/article/post' component={CreateArticle} />
        <Route path='/login' render={() => <LogIn onLogIn={onLogIn} />}/>
        <Route exact path='/signup' component={SignUp} />
      </Switch>
    </Router>
  )
}



export default App;
