import React, { useState, useEffect } from 'react'
import { Link, useLocation } from 'react-router-dom'

const SideNav = () => {
    const location = useLocation().pathname;
    const [opt, setOpt] = useState(0);

    useEffect(()=>{
        location==='/'?
            setOpt(0)
            :
            location.indexOf('major')?
            setOpt(1)
            :
            location.indexOf('dream')?
            setOpt(2)
            :
            setOpt(3)
    })

    return (
        <div class="col-lg-3">
            <h1 class="my-4">Articles</h1>
            <div class="list-group">
                <Link to='/' class={"list-group-item"+opt==0?" active":""}>Recent</Link>
                <Link to='/' class={"list-group-item"+opt==1?" active":""}>Major</Link>
                <Link to='/' class={"list-group-item"+opt==2?" active":""}>Dream</Link>
            </div>
        </div>
    )
}
export default SideNav;
