import React, {useState, useEffect} from 'react'
import EditMyInfo from '../components/EditMyInfo';
import MyInfo from '../components/MyInfo';
import SideNav from '../components/SideNav';
import request from '../libs/request'

const Profile = () => {
    const [opt, setOpt] = useState(0);
    const [data, setData] = useState();

    useEffect(()=>{
        async function fetchData(){
            let temp = await request.getUser(window.sessionStorage.getItem('n_name'));
            setData(temp);
        }
        fetchData();
    }, []);

    useEffect(()=>{
        async function fetchData() {
            let temp;
            switch(opt)
            {
                case 3:
                    temp = await request.getUser(window.sessionStorage.getItem('n_name'));
                    break;
                case 4:
                    temp = await request.getArticlesByMajor(window.sessionStorage.getItem('major'));
                    break;
                case 5:
                    temp = await request.getArticlesByDream(window.sessionStorage.getItem('dream'));
                    break;
                case 6:
                    temp = await request.getArticlesByDream(window.sessionStorage.getItem('dream'));
                    break;
                default:
                    temp = await request.getArticles();
            }
            setData(temp);
            console.log(temp);
        }
        fetchData();
    }, [opt]);

    const optHandler = (option) => {
        setOpt(option);
    }

    return(
        <div class="container">
            <div class="row">

                <SideNav onHandle={optHandler} />

                <div class="col-lg-8 col-md-10 mx-auto">

                    {opt === 3 &&
                        <MyInfo data={data} />
                    }

                    {opt === 6 &&
                        <EditMyInfo data={data} onSubmit={request.updateUser}/>
                    }

                </div>
            </div>
        </div>
    )
}

export default Profile;
