import React, { useRef } from 'react'
import {withRouter} from 'react-router-dom'

const LogInForm = (props) => {
    const n_name = useRef();
    const passwd = useRef();

    const submit = async() => {
        const formData = {
            n_name : n_name.current.value,
            passwd : passwd.current.value,
        }
        //console.log(formData);
        const result = await props.onSubmit(formData);
        console.log(result.session);

        if(!result.session.n_name);
        else {
            window.sessionStorage.setItem('n_name', result.session.n_name);
            props.onLogIn();
        }

        props.history.push('/');
    }
    const cancel = () => {
        props.history.goBack();
    }

    return(
        <>
        <div name="sentMessage" id="contactForm" novalidate>
          <div class="control-group">
            <div class="form-group floating-label-form-group controls">
                <label>Nick_Name</label>
              <input type="text" class="form-control" ref={n_name} placeholder="Nick_Name" id="name" required data-validation-required-message="Please enter your name." />
            </div>
          </div>
          <div class="control-group">
            <div class="form-group floating-label-form-group controls">
                <label>Password</label>
              <textarea class="form-control" cols="50" rows="5" ref={passwd} placeholder="Password" required data-validation-required-message="Please enter your email address." />
              <p class="help-block text-danger"></p>
            </div>
          </div>
          <br />
          <div id="success"></div>
          <button onClick={()=>submit()} class="btn btn-primary" id="sendMessageButton">Send</button>
          &nbsp;&nbsp;&nbsp;&nbsp;
          <button class="mg-bottom-3" onClick={()=>cancel()} class="btn btn-primary" id="sendMessageButton">Cancel</button>
        </div>
            <div>Nickname : <input ref={n_name}></input></div>
            <div>Password : <input ref={passwd} type="password"></input></div>
            <button onClick={()=>submit()}>작성</button>
            <button onClick={()=>cancel()}>취소</button>
        </>
    )
}

export default withRouter(LogInForm);
