import React, { useState, useEffect } from 'react'
import {Link, useParams, useLocation} from 'react-router-dom'
import request from '../libs/request'
import ArticleS from '../components/ArticleS'

const ArticleSpecific = (props) => {
    const [article, setArticle] = useState();
    const {id} = useParams();
    const location = useLocation();
    useEffect(()=>{
        /*
        async function fetchData(){
            console.log(id);
            let temp = await request.getArticle(location.state);
            console.log(temp);
            setArticle(temp);
            headChange();
        }
        fetchData();
        */
       async function fetchData(){
        setArticle(location.state);
        headChange();
    }
    },[])
    const headChange = () => {
        props.setHeader(article);
    }
    return(
        <div class="container">
            <div class="row">
                {console.log(article)}
                <div class="col-lg-3">
                    <h1 class="my-4">Caterogies</h1>
                    <div class="list-group">
                        <Link to='/' class="list-group-item active">Category 1</Link>
                        <Link to='/' class="list-group-item">Category 2</Link>
                        <Link to='/' class="list-group-item">Category 3</Link>
                    </div>
                </div>
                <div class="col-lg-8 col-md-10 mx-auto">

                    <ArticleS article={article}/>

                </div>
            </div>
        </div>
    )
}

export default ArticleSpecific;
