import React, { useState, useEffect } from 'react'
import request from '../libs/request'
import List from '../components/List'
import { withRouter, Link } from 'react-router-dom'

const Articles = (props) => {
    const [list, setList] = useState([]);

    useEffect(() => {
        async function fetchData() {
            let articleList = await request.getArticles();
            setList(articleList);
        }
        fetchData();
    }, []);

    return (
        <div class="container">
            <div class="row">

                <div class="col-lg-3">
                    <h1 class="my-4">Caterogies</h1>
                    <div class="list-group">
                        <Link to='/' class="list-group-item active">Category 1</Link>
                        <Link to='/' class="list-group-item">Category 2</Link>
                        <Link to='/' class="list-group-item">Category 3</Link>
                    </div>
                </div>
                <div class="col-lg-8 col-md-10 mx-auto">

                    <List list={list} />

                </div>
            </div>
        </div>
    )
}

export default withRouter(Articles);
