import React, { useState, useEffect } from 'react'
import moment from 'moment'
import { useLocation } from 'react-router-dom'

const Header = (props) => {
    const [article, setArticle] = useState();
    //const {author, date, title} = props.article;
    const location = useLocation().pathname;
    useEffect(()=>{
        setArticle(props.article);
    },[])
    return (
        <header class="masthead" id="home">
            <div class="overlay"></div>
            <div class="container">
                <div class="row">
                    <div class="col-lg-8 col-md-10 mx-auto">
                        <div class="site-heading">
                            {location === '/login' &&
                                <>
                                    <h1>Login</h1>
                                    <span class="subheading">로그인하셈ㅋ</span>
                                </>
                            }
                            {location === '/' &&
                                <>
                                    <h1>Articles</h1>
                                    <span class="subheading">게시물들임ㅋ</span>
                                </>
                            }
                            {location === '/signup' &&
                                <>
                                    <h1>Sign Up</h1>
                                    <span class="subheading">가입하셈ㅋ</span>
                                </>
                            }
                            {location === '/article/post' &&
                                <>

                                    <h1>Posting</h1>
                                    <span class="subheading">올리셈ㅋ</span>

                                </>
                            }
                            {location.indexOf('/article/') !== -1 &&
                                article?
                                <>
                                    {console.log(props.article)}
                                    <h1>{props.article.title}</h1>
                                    <span class="meta">Posted by {props.article.author.n_name} on {moment(props.article.date).format('YYYY-MM-DD, hh:mm')}</span>
                                    {console.log(article)}
                                </>
                                :
                                null
                            }
                        </div>
                    </div>
                </div>
            </div>
        </header>
    )
}

export default Header;
