import axios from 'axios'

const API_DEFAULT = "http://localhost:3030/";
const instance = axios.create({ baseURL : API_DEFAULT });

export async function getArticles() {
    const result = await instance.get('/');
    return result.data;
}
export async function createArticle( {title, author, content} ) {
    const result = await instance.post('/article/create', {title, author, content});
    return result.data;
}

export default {
    getArticles
}
