import React, { useState, useEffect } from 'react'
import request from '../libs/request'
import List from '../components/List'
import { withRouter } from 'react-router-dom'

const Articles = (props) => {
    const [list, setList] = useState([]);
    const posting = () => {
        if (window.sessionStorage.getItem('n_name')) props.history.push('/article/post');
        else {
            window.alert('로그인이 필요합니다!');
            props.history.push('/login');
        }
        useEffect(() => {
            async function fetchData() {
                let articleList = await request.getArticles();
                setList(articleList);
            }
            fetchData();
        }, []);
        return (
            <div class="container">
                <div class="row">

                    <div class="col-lg-3">
                        <h1 class="my-4">Caterogies</h1>
                        <div class="list-group">
                            <a href="#" class="list-group-item active">Category 1</a>
                            <a href="#" class="list-group-item">Category 2</a>
                            <a href="#" class="list-group-item">Category 3</a>
                        </div>
                    </div>
                    <div class="col-lg-8 col-md-10 mx-auto">

                        <List list={list} />
                        <div class="clearfix">
                            <button class="btn btn-primary float-right" onClick={() => posting()}>Post &rarr;</button>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default withRouter(Articles);
