import React from 'react'
import Star from './Star'
import moment from 'moment'
import {Link} from 'react-router-dom'

const Article = (props) => {
    const { article } = props;
    const content = article.content.length>40?article.content.substr(0,40) + '...':article.content;
    return(
        <div class="post-preview">
          <Link to={{
            pathname: `/article/${article._id}`,
            state : article
          }}>
            <h2 class="post-title">
              {article.title}
            </h2>
            <h5 class="post-content">
              {content}
            </h5>
          </Link>
          <p class="post-meta">Posted by <Link to="/">{article.author.n_name}</Link> on {moment(article.date).format('YYYY-MM-DD, hh:mm')}</p>
            <span class="text-warning">
                <Star rating={article.rating} />
            </span>
            <hr />
        </div>
    )
}

export default Article;
